+            // Find all matches
+            lines.forEach((line, lineIndex) => {
+                const trimmedLine = line.trim().toLowerCase();
+                if (trimmedLine.includes(searchTerm)) {
+                    // Calculate character position in textarea
+                    let charPosition = 0;
+                    for (let i = 0; i < lineIndex; i++) {
+                        charPosition += lines[i].length + 1; // +1 for newline character
+                    }
+                    
+                    searchMatches.push({
+                        lineIndex: lineIndex,
+                        charStart: charPosition,
+                        charEnd: charPosition + line.length,
+                        line: line.trim(),
+                        matchStart: charPosition + line.toLowerCase().indexOf(searchTerm),
+                        matchEnd: charPosition + line.toLowerCase().indexOf(searchTerm) + searchTerm.length
+                    });
+                }
+            });
+            
+            // Update search results display
+            if (searchMatches.length > 0) {
+                currentMatchIndex = 0;
+                searchResults.style.display = 'block';
+                updateSearchResultsDisplay();
+                highlightMatch(currentMatchIndex);
+            } else {
+                searchResults.style.display = 'block';
+                searchResultText.textContent = `Nenhum resultado encontrado para "${searchBox.value}"`;
+            }
+        }
+    }
+
+    function updateSearchResultsDisplay() {
+        const searchResultText = document.getElementById('searchResultText');
+        const searchBox = document.getElementById('emailSearchBox');
+        
+        if (searchMatches.length > 0) {
+            searchResultText.textContent = `${currentMatchIndex + 1} de ${searchMatches.length} resultados para "${searchBox.value}"`;
+        }
+    }
+
+    function highlightMatch(matchIndex) {
+        if (matchIndex < 0 || matchIndex >= searchMatches.length) return;
+        
+        const textarea = document.getElementById('bulkEmails');
+        const match = searchMatches[matchIndex];
+        
+        // Set cursor position and select the match
+        textarea.focus();
+        textarea.setSelectionRange(match.matchStart, match.matchEnd);
+        
+        // Scroll to make the selection visible
+        const textBeforeMatch = textarea.value.substring(0, match.matchStart);
+        const linesBefore = textBeforeMatch.split('\n').length - 1;
+        const lineHeight = 20; // Approximate line height
+        const scrollTop = Math.max(0, (linesBefore - 2) * lineHeight);
+        
+        textarea.scrollTop = scrollTop;
+    }
+
+    function navigateSearchResults(direction) {
+        if (searchMatches.length === 0) return;
+        
+        if (direction === 'next') {
+            currentMatchIndex = (currentMatchIndex + 1) % searchMatches.length;
+        } else if (direction === 'prev') {
+            currentMatchIndex = currentMatchIndex <= 0 ? searchMatches.length - 1 : currentMatchIndex - 1;
+        }
+        
+        updateSearchResultsDisplay();
+        highlightMatch(currentMatchIndex);
+    }
+
+    function handleSearchNavigation(event) {
+        if (event.key === 'Enter') {
+            event.preventDefault();
+            if (event.shiftKey) {
+                navigateSearchResults('prev');
+            } else {
+                navigateSearchResults('next');
+            }
+        } else if (event.key === 'Escape') {
+            clearEmailSearch();
+        }
+    }
+
+    function clearEmailSearch() {
+        const searchBox = document.getElementById('emailSearchBox');
+        const searchResults = document.getElementById('searchResults');
+        const textarea = document.getElementById('bulkEmails');
+        
+        searchBox.value = '';
+        searchResults.style.display = 'none';
+        searchMatches = [];
+        currentMatchIndex = -1;
+        lastSearchTerm = '';
+        
+        // Clear selection in textarea
+        if (textarea.selectionStart !== textarea.selectionEnd) {
+            textarea.setSelectionRange(textarea.selectionEnd, textarea.selectionEnd);
+        }
+    }
