+            .then(data => {
+                if (data.error) {
+                    alert(` Erro: ${data.error}`);
+                    return;
+                }
+
+                const list = data.lists.find(l => l.list_name === listName);
+                if (!list) {
+                    alert(` Lista "${listName}" no encontrada`);
+                    return;
+                }
+
+                const textarea = document.getElementById('bulkEmails');
+                const emailList = list.emails.filter(email => email && email.trim()).join('\n');
+                textarea.value = emailList;
