+                // Clear any existing search when loading new list
+                clearEmailSearch();
+                
+                alert(` Lista "${listName}" carregada com ${list.email_count} emails`);
+            })
+            .catch(error => {
+                console.error('Error:', error);
+                alert(` Erro ao carregar lista: ${error.message}`);
+            });
+    }
